import { Component } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog';
import { DeleteService } from '../services/DeleteService';

@Component({
  selector: 'app-delete-confirmation-dialog',
  templateUrl: 'delete-confirmation-dialog.component.html',
  styleUrls: ['./delete-confirmation-dialog.component.css'], // Importez le fichier CSS
})
export class DeleteConfirmationDialogComponent {
  loggedInUserLogin!:string;
  constructor(public dialogRef: MatDialogRef<DeleteConfirmationDialogComponent>,
              private deleteService:DeleteService) {}

  ngOnInit(){
    this.deleteService.getCurrentUserLogin().subscribe((login) => {
      this.loggedInUserLogin = login;
    });
  }

  deleteButton() {
    if (this.loggedInUserLogin) {
      this.deleteService.deleteMyUser(this.loggedInUserLogin).subscribe(() => {
        console.log('Compte supprimé !');
      });
    }
  }

  confirmDelete(): void {
    this.dialogRef.close('delete');
  }

  cancel(): void {
    this.dialogRef.close();
  }
}
<div class="dialog-custom-container">
    <h1 mat-dialog-title class="dialog-custom-title">Confirmation de suppression</h1>
    <div mat-dialog-content class="dialog-custom-content">
      <p>Êtes-vous sûr de vouloir supprimer votre compte ?</p>
    </div>
    <div mat-dialog-actions class="dialog-custom-actions">
      <button mat-button (click)="cancel()" class="custom-dialog-button">Annuler</button>
      <button mat-button  (click)="confirmDelete()" (click)="deleteButton()" mat-button routerLink="/se-connecter" color="warn" class="custom-dialog-button">Supprimer</button>
    </div>
</div>
